package com.xr.telegram

import adaptr.ChatAdapter
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import model.User


class SecondFragment : Fragment() {
    lateinit var userList:ArrayList<User>


    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_second,container,false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initViews(view)
    }

    private fun initViews(view: View) {
        val rvSecond = view.findViewById<RecyclerView>(R.id.rv_chat_list_second)
        loadList()
        rvSecond.layoutManager= LinearLayoutManager(context, LinearLayoutManager.VERTICAL,false)
        rvSecond.adapter= ChatAdapter(userList)
    }

    private fun loadList() {
        userList= ArrayList()
        userList.add(User("data:image/jpeg;base64,","Ledi Gaga","today","Hi bro, How are you today?",true))
        userList.add(User("data:image/jpeg;base64,","Messi","today","Hi bro, How are you today?",false))
        userList.add(User("data:image/jpeg;base64,","Ronaldo","today","Hi bro, How are you today?",true))

        userList.add(User("data:image/jpeg;base64,","Barak Abama","today","Hi bro, How are you today?",true))
        userList.add(User("data:image/jpeg;base64,","Messi","today","Hi bro, How are you today?",false))
        userList.add(User("data:image/jpeg;base64,","Ronaldo","today","Hi bro, How are you today?",true))

    }
}